import React, {useState} from "react";

const Main = () => {
  const [flagDaily, setFlagDaily] = useState(false);
  const [flagMonthly, setFlagMonthyl] = useState(false);

  let data = require("../../data.json");

  const clickDaily = () => {
    setFlagDaily(!flagDaily);
  };

  const clickWeekly = () => {
    setFlagDaily(!flagDaily);
  };

  const clickMonthly = () => {
    setFlagDaily(!flagDaily);
  };

  return (
    <>
      <div className="mainWrap flex-row">
        <div className="boxLef flex-col">
          <div className="flex-col">
            <div> </div>
            <div>
              <span id="report">Report for </span> <br /> Jeremy Robson
            </div>
          </div>
          <div>
            <ul>
              <li>
                <button type="button" onClick={clickDaily}>Daily</button>
              </li>
              <li>
                <button type="button" onClick={() => clickWeekly()}>Weekly</button>
              </li>
              <li>
                <button type="button" onClick={() => clickMonthly()}>Monthly</button>
              </li>
            </ul>
          </div>
        </div>
        <div className="boxRig">
          {data.map((e, i) => (
            <div
              className="gridBox"
              style={{
                backgroundColor: data[i].bgclr,
                backgroundImage: "url(" + `icon${i + 1}.svg` + ")",
              }}
            >
              <div>
                <div>
                  <span>{e.title}</span>
                  <img src={`./icon-ellipsis.svg`} />
                </div>
                <div>{e.timeframes.weekly.current}hrs</div>
                {flagDaily && (
                  <div> Last Day - {e.timeframes.weekly.current}hrs</div>
                )}
                {flagMonthly ? (
                  <div> Last Monthy - {e.timeframes.weekly.current * 4}hrs</div>
                ) : 
                  <div> Last Week - {e.timeframes.weekly.current}hrs</div>
                }

              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default Main;
